import React, { Component } from 'react';
import PropTypes from 'prop-types';

class PostComment extends Component {
  state={
    body:""
  }
  render() {
    return (
      <div>
        <form action="" onSubmit={this.handleSubmit}>
          <textarea name="" id="" cols="30" rows="10" style={{ width: "876px", height: "59px" }} placeholder="what do you think? ..." onChange={this.handleChange}></textarea>
      <button type="submit">send</button>
        </form>
      </div>
    );
  }
  handleChange=e =>{
    this.setState({body:e.target.value})
  }
  handleSubmit=e=>{
    e.preventDefault();
    this.postComment();
  }
}

