import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Link } from '@reach/router';
import './PostArticle.css';
import * as api from '../api'

class PostArticle extends Component {
  state = {
    title: "",
    body: ""
  }
  render() {
    return (
      <div >
        <form className="post-article-form" action="" onSubmit={this.handleSubmit}>
          <label htmlFor="title">Title: </label>
          <input type="text" name="title" id="title" onChange={this.handleChangle} />
          <label htmlFor="body">Body: </label>
          <textarea name="" id="" cols="30" style={{ width: "521px", height: "182px" }} rows="10" onChange={this.handleChangle}>
            write your article ...</textarea>
            <button type="submit">Add Article</button>
        </form>
      </div>
    )
  }
  handleChangle = e => {
    const { id, value } = e.target
    this.setState({
      [id]: value
    })
  }
  handleSubmit = e => {
    e.preventDefault();
    this.postArticle();

  }
  postArticle = () => {
    api.postArticleByTopic(this.state.title, this.state.body)

  }
}

PostArticle.propTypes = {

};

export default PostArticle;
