        })
      })
  }
  postComment = () => {
    api.postCommentByArticleId(this.props.id, {
        body: this.state.body,
        created_by: this.props.user._id
      })
      .then(comment => {
        this.setState({
           comments:[this.state.body, ...this.state.comments]
        })
      });
  };


}

