import React, { Component } from 'react';
import PropTypes from 'prop-types';
import * as api from '../api';
import './Articles.css'
import {Link} from '@reach/router'

class Articles extends Component {
  state = {
    articles: []
  }
  render() {
    const { articles } = this.state;
    return (
      <main>
        ARTICLES
        {articles.map(({_id,title,body,created_by,created_at,belongs_to}) => {
          return (
            <li key={_id}>
            <h3>{title}</h3>
            <h5>Posted by: {created_by.username}</h5>
            <p>{body}</p> 
            <h6>{_id}</h6>
              <Link to={`/articles/${_id}`}><h4>See more</h4>
            </Link>

            </li>
          )
        }
        )}
      </main>
    );
  }
  componentDidMount() {
    this.fetchArticles();
  }
  fetchArticles = () => {
    console.log("inside articles")
    api.getArticles()
      .then(
        articles=>{
          this.setState({
            articles
          })
        }
      )
    }
}

Articles.propTypes = {

};

export default Articles;
