import React, { Component } from 'react';
import PropTypes from 'prop-types';
import * as api from '../api'

class Comments extends Component {
  state={
    comments:[]
  }
  render() {
    return (
      <div>

      </div>
    );
  }
  componentDidMount(){
    this.fetchComments()
  }

  fetchComments=() =>{
    api.getCommentsByArticleId()
    .then(comments=>{
      this.setState({
        comments
      })
    })
  }

}

Comments.propTypes = {

};

export default Comments;
