import React, { Component } from 'react';
import PropTypes from 'prop-types';
import './Login.css'
class Login extends Component {
  state={
    username:''
  }
  render() {
    if(this.props.user === this.state.username) return this.props.children;
    return (
      <div id="login" className="main">
        <form action=""onSubmit={this.handleSubmit} className="inputButton">
        <input type="text" name="username" id="username" onChange={this.handleChange}/> <br/>
        <button type="submit">Login</button>
        </form>
      </div>
    );
  }
  handleChange = e => {
    this.setState({
      username:e.target.value
    })
  }
  handleSubmit= e => {
    e.preventDefault();
    this.props.login(this.state.username)
    this.setState({
      username:''
    })

  }
}

Login.propTypes = {

};

export default Login;
