import React, { Component } from "react";
import * as api from "../api";
import PropTypes from "prop-types";

class Vote extends Component {
  state = {
    vote: 0
  };
  render() {
    return <div>
        <h4>Likes: {this.props.votes + this.state.vote}</h4>
      <button onClick={() => this.voteChange(this.props.id, "up")} disabled={this.state.vote === 1}>
          Like
        </button>
        <button onClick={() => this.voteChange(this.props.id, "down")} disabled={this.state.vote === -1}>
          dislike
        </button>
      </div>;
  }
  voteChange = (id, direction) => {
    api.updateArticleVote(id, direction).catch(err => {
      this.setState(err);
    });

    this.setState({
      vote: direction === "Like" ? this.state.vote + 1 : this.state.vote - 1
    });
  };
}
Vote.propTypes = {
  voteMod: PropTypes.number
};
export default Vote;
